import { Component } from '@angular/core';

import { Tutor } from '../../model/tutor.model';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';
import { CardTutorComponent } from "../card-tutor/card-tutor.component";
import { NgFor } from '@angular/common';
import { PaginationComponent } from "../../../../shared/components/pagination/pagination.component";


@Component({
  selector: 'app-list-tutor-accueil',
    imports: [
    NgFor,
    CardTutorComponent,
    PaginationComponent
],
  templateUrl: './list-tutor-accueil.component.html',

})
export class ListTutorAccueilComponent {
  tutors: Tutor[] = [
    {
      id: 1,
      introduce_linkyoutube:"MGKGjjJXEoI",
      name: 'Sarah Johnson',
      avatar: 'data:image/jpeg;base64,',
      rating: 4.9,
      specialization: 'Business English, IELTS',
      language: 'Native English Speaker',
      students: 245,
      bio: 'Certified TEFL instructor with 8 years experience helping professionals improve their business communication skills. Specialized in interview preparation and presentation coaching.',
      price: 28
    },
    {
      id: 2,
      name: 'Michael Chen',
      avatar: 'data:image/jpeg;base64,',
      rating: 4.8,
      specialization: 'Conversational English',
      language: 'Bilingual (English/Mandarin)',
      students: 189,
      bio: 'Friendly and patient tutor focusing on natural conversation skills. I create customized lessons based on your interests and goals.',
      price: 22,
      introduce_linkyoutube:'euj-eCDkQI4'
    },

  ];
  selectedTutor: Tutor | null = null;
  constructor(private sanitizer: DomSanitizer) {}

  playVideo(tutor: Tutor) {
    this.selectedTutor = tutor;
  }

  closeVideo() {
    this.selectedTutor = null;
  }

  getSafeEmbedUrl(videoId: string): SafeResourceUrl {
    const url = `https://www.youtube.com/embed/${videoId}?autoplay=1`;
    return this.sanitizer.bypassSecurityTrustResourceUrl(url);
  }

  getVideoId(url: string): string {
    // Extract YouTube video ID from various URL formats
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[2].length === 11) ? match[2] : '';
  }
}
